(()=>{const e=[{description:"Clean the house",completed:!1,index:1},{description:"Wash the dishes",completed:!0,index:2},{description:"Wash the car",completed:!1,index:3}];document.addEventListener("DOMContentLoaded",(function(){const t=document.getElementById("task-list");e.sort(((e,t)=>e.index-t.index));for(let n=0;n<e.length;n+=1){let i=e[n];const d=document.createElement("li");d.textContent=i.description,d.innerHTML=`\n    <div class='list-container'>\n        <input type='checkbox'>\n        <label>${i.description}</label>\n        <div class="task-actions">\n          <i class="fas fa-ellipsis-v"></i>\n        </div>\n      </div>\n\n    `,i.completed&&d.classList.add("completed"),t.appendChild(d)}}))})();